interface AvatarProps {
  top?: string;
  bottom?: string;
  onePiece?: string;
  className?: string;
}

export default function Avatar({ top, bottom, onePiece, className }: AvatarProps) {
  return (
    <div className={`relative ${className} bg-gray-200 rounded-lg overflow-hidden flex items-center justify-center`}>
      {/* Human Figure Placeholder */}
      <svg className="w-full h-full text-gray-400" fill="currentColor" viewBox="0 0 168 300">
        <path d="M84 22c-7.2 0-13 5.8-13 13s5.8 13 13 13 13-5.8 13-13-5.8-13-13-13zm-2.2 38.3c-24.2 0-43.8 19.6-43.8 43.8v79.4c0 10.2 8.3 18.5 18.5 18.5h54c10.2 0 18.5-8.3 18.5-18.5v-79.4c0-24.2-19.6-43.8-43.8-43.8h-3.4zM64.5 210.5v67c0 4.1 3.4 7.5 7.5 7.5h24c4.1 0 7.5-3.4 7.5-7.5v-67h-39z"/>
      </svg>

      {onePiece ? (
        <img src={onePiece} alt="One Piece" className="absolute inset-0 w-full h-full object-contain" />
      ) : (
        <>
          {top && <img src={top} alt="Top" className="absolute top-0 left-0 w-full h-1/2 object-contain" />}
          {bottom && <img src={bottom} alt="Bottom" className="absolute bottom-0 left-0 w-full h-1/2 object-contain" />}
        </>
      )}
    </div>
  );
}
